import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Container, Row, Col, Button, Image, Card } from 'react-bootstrap';
import AppCarousel from "./components/SkincareBasics"
import AppNavbar from "./components/Navbars"
import AppAIAnalysis from "./components/AIAnalysis"
import AppProductFinder from "./components/ProductFinder"


function App() {

	var products = [
		{id: 1, name: "Cleanser", image:"images/product1.png"},
		{id: 2, name: "Serum", image:"images/product2.png"},
		{id: 3, name: "Moisturiser", image:"images/product3.png"},
		{id: 4, name: "Sunscreen", image:"images/product4.png"},
	];


	return (

	<>

	{/* ----------------------------- Navbar Section -------------------------------------- */}

		<AppNavbar />


	{/* ----------------------------- AI Section -------------------------------------- */}

		<AppAIAnalysis />


	{/* ----------------------------- Shop Section -------------------------------------- */}
		<section>
			<Container>
				<h2 className="shop-heading text-center">Customise Your Skincare Regimen</h2>
				<Row>
					{products.map(product =>(	
						<Col key={product.id}>
							<Card>
								<Card.Body className="shop-products text-center my-3">
									<Card.Title>{product.name}</Card.Title>
									<Card.Img src={product.image}/>
									<Button variant="outline-dark">Shop Now</Button>
								</Card.Body>
							</Card>
						</Col>
					))}
				</Row>
			</Container>
		</section>



	{/* ----------------------------- Product Finder Section -------------------------------------- */}

		<AppProductFinder />


	{/* ----------------------------- Blog Section -------------------------------------- */}

		<AppCarousel />



	{/* ----------------------------- Why Us Section -------------------------------------- */}
	<Container>
	<h2 className= "why-us text-center">Why Us</h2>
	<p className= "why-para text-center">“Your Skin, Our Science.”</p>
	<p className= "why-para text-center">
		At SkinShots, we combine dermatology expertise with cutting-edge AI technology to deliver skincare that truly understands you. 
		From personalized product recommendations to advanced skin analysis, every solution is backed by science and tailored for your unique needs. 
		Trusted ingredients, transparent results, and a commitment to healthy, glowing skin—because you deserve nothing less.
	</p>
	</Container>

	</>
  );
}
 
export default App;
