import 'bootstrap/dist/css/bootstrap.min.css';
import "../App.css"

import React from "react";
import {useCart} from "../context/CartContext";
import {Button, Image} from "react-bootstrap";

// import api from "../api/api"; // wrapper for fetch/axios

export default function CheckoutPage() {

  const { cart, getTotal } = useCart();

  const handleCheckout = async () => {
    alert("Checkout coming soon!");

    // send cart to backend to create payment session
    // const res = await api.post("/create-checkout-session", { items: cart });
    // backend returns { url: "https://checkout.stripe.com/..." }
    // window.location.href = res.data.url;
  };

  return (
    <div>
        {cart.map(item => (
            <div key={`${item.category}-${item.id}`}>
                <Image className="checkout-product-images" src = {item.image} rounded fluid/>
                {item.name} x {item.quantity} — ₹{item.price}
            </div>      
        ))}
        <h3>Total: ₹{getTotal()}</h3>
        <Button 
            onClick={handleCheckout}
        >Pay Securely</Button>
    </div>
  );
}
