import 'bootstrap/dist/css/bootstrap.min.css';
import "../App.css"

import React from "react";
import {useCart} from "../context/CartContext";
import {Button, Image} from "react-bootstrap";


export default function CheckoutPage() {

  const { cart, addToCart, removeFromCart, updateQuantity, clearCart, getTotal } = useCart();

  const handleCheckout = async () => {
    alert("Checkout coming soon!");
  };

  return (
    <div className="checkout-page">
        <h2>Your Shopping Cart</h2>
        {cart.map(item => (
            <div className = "checkout-products" key={`${item.category}-${item.id}`}>
                <Image className="checkout-product-images" src = {item.image} rounded fluid/>
                {item.name} x {item.quantity} — ₹{item.price}
                <Button 
                    className="product-del-button"
                    size="sm"
                    onClick={()=> removeFromCart}> ╳ </Button>
            </div>      
        ))}
        <h3>Total: ₹{getTotal()}</h3>
        <Button 
            className="checkout-page-button" 
            size="lg"
            onClick={handleCheckout}
        >Pay Securely</Button>
    </div>
  );
}
